// Enhanced customer information extraction with session management
const emailData = $('Enhanced Gmail Trigger').item.json;
const sapSession = $('SAP B1 - Login').item.json;
const classification = $('Enhanced Email Classifier').item.json;

// Store session creation time for timeout management
const sessionCreatedAt = new Date().toISOString();
const sessionTimeout = sapSession.SessionTimeout || 30; // 30 minutes default

// Extract customer info with null safety
const customerInfo = {
  email: emailData.from?.value?.[0]?.address?.toLowerCase().trim() || '',
  name: emailData.from?.value?.[0]?.name?.trim() || '',
  subject: emailData.subject || '',
  body: emailData.text || '',
  display_name: emailData.from?.value?.[0]?.name || 'Unknown'
};

// Clean email address (remove extra characters)
if (customerInfo.email) {
  customerInfo.email = customerInfo.email.replace(/[<>]/g, '');
}

// Extract potential customer codes from email body
const customerCodePattern = /(?:customer|client|bp|partner)[\s#:]*([A-Z0-9]{3,10})/i;
const codeMatch = customerInfo.body.match(customerCodePattern);
const potentialCustomerCode = codeMatch ? codeMatch[1] : null;

return [{
  json: {
    customer_info: customerInfo,
    potential_customer_code: potentialCustomerCode,
    sap_session: {
      ...sapSession,
      created_at: sessionCreatedAt,
      timeout_minutes: sessionTimeout,
      expires_at: new Date(Date.now() + (sessionTimeout * 60 * 1000)).toISOString()
    },
    classification: classification,
    sap_credentials: {
      base_url: 'https://172.16.0.217:50000/b1s/v1',
      company_db: 'GOTO_TEST',
      username: 'manager',
      password: 'infor'
    }
  }
}];

// Check if SAP B1 session is still valid (within 30-minute timeout)
const sessionData = $json.sap_session;
const now = new Date();
const sessionCreated = new Date(sessionData.created_at);
const sessionTimeout = sessionData.timeout_minutes || 30;
const minutesElapsed = (now - sessionCreated) / (1000 * 60);

// If session is expired or close to expiring (within 2 minutes), need to refresh
const needsRefresh = minutesElapsed >= (sessionTimeout - 2);

return [{
  json: {
    ...{$json},
    session_check: {
      needs_refresh: needsRefresh,
      minutes_elapsed: minutesElapsed,
      minutes_remaining: sessionTimeout - minutesElapsed,
      session_valid: !needsRefresh
    }
  }
}];

// Update session data after refresh
const originalData = $('Function - Check Session Validity').item.json;
const newSession = $json;

// Update with new session info
const sessionCreatedAt = new Date().toISOString();
const sessionTimeout = newSession.SessionTimeout || 30;

return [{
  json: {
    ...originalData,
    sap_session: {
      ...newSession,
      created_at: sessionCreatedAt,
      timeout_minutes: sessionTimeout,
      expires_at: new Date(Date.now() + (sessionTimeout * 60 * 1000)).toISOString()
    },
    session_check: {
      needs_refresh: false,
      minutes_elapsed: 0,
      minutes_remaining: sessionTimeout,
      session_valid: true,
      refreshed: true
    }
  }
}];

// Enhanced debugging for customer matching
const customerMatchResult = $json;
const originalData = $('Merge Session Data').first().json;
const businessPartnersData = $('SAP B1 - Search Business Partners').first().json;

// ADD COMPREHENSIVE DEBUG LOGGING
console.log("=== DETAILED DEBUG INFO ===");
console.log("Sender Email from originalData:", originalData.$json.customer_info.email);
console.log("Business Partners Response:", JSON.stringify(businessPartnersData, null, 2));

// Check if we have business partners data
if (businessPartnersData.value && businessPartnersData.value.length > 0) {
  console.log("Number of Business Partners found:", businessPartnersData.value.length);

  // Check each business partner for email match
  businessPartnersData.value.forEach((partner, index) => {
    console.log(`Partner ${index + 1}:`);
    console.log(`  CardCode: ${partner.CardCode}`);
    console.log(`  CardName: ${partner.CardName}`);
    console.log(`  EmailAddress: ${partner.EmailAddress}`);
    console.log(`  Email Match: ${partner.EmailAddress === originalData.$json.customer_info.email}`);
  });
} else {
  console.log("No business partners found in response");
}

console.log("AI Customer Match Result:", JSON.stringify(customerMatchResult, null, 2));
console.log("Customer matched?", customerMatchResult.matched);
console.log("Customer code:", customerMatchResult.customer_code);
console.log("Match confidence:", customerMatchResult.confidence);
console.log("Match reasoning:", customerMatchResult.reasoning);

// If no match found, let's do a manual check
if (!customerMatchResult.matched && businessPartnersData.value) {
  const manualMatch = businessPartnersData.value.find(partner => 
    partner.EmailAddress && partner.EmailAddress.toLowerCase().trim() === 
    originalData.$json.customer_info.email.toLowerCase().trim()
  );

  if (manualMatch) {
    console.log("MANUAL MATCH FOUND - AI failed to detect it!");
    console.log("Manual match result:", {
      CardCode: manualMatch.CardCode,
      CardName: manualMatch.CardName,
      EmailAddress: manualMatch.EmailAddress
    });

    // Override the AI result with manual match
    return [{
      json: {
        ...originalData,
        customer_match: {
          matched: true,
          confidence: 100,
          customer_code: manualMatch.CardCode,
          customer_name: manualMatch.CardName,
          customer_email: manualMatch.EmailAddress,
          reasoning: "Manual fallback match by email address",
          ai_failed: true,
          original_ai_result: customerMatchResult
        }
      }
    }];
  }
}

return [{
  json: {
    ...originalData,
    customer_match: customerMatchResult
  }
}];

