// Enhanced failure analysis and intelligent ticket creation with safe node referencing
try {
  // Safely get data from nodes that may or may not have executed
  let emailData, classification, customerMatchAttempt;

  // Try to get email data
  try {
    emailData = $('Enhanced Gmail Trigger').first().json;
  } catch (e) {
    // Fallback: try to get from current execution context
    emailData = $input.first()?.json?.email_data || {
      from: { value: [{ address: 'unknown@example.com', name: 'Unknown Customer' }] },
      subject: 'Unknown Request',
      text: 'Email content unavailable'
    };
  }

  // Try to get classification data
  try {
    classification = $('Enhanced Email Classifier').first().json;
  } catch (e) {
    // Fallback classification
    classification = {
      is_invoice_request: true,
      order_number: null,
      request_type: 'unknown',
      urgency: 'normal',
      confidence: 0
    };
  }

  // Try to get customer matching data
  try {
    customerMatchAttempt = $('AI Customer Matching').first()?.json;
  } catch (e) {
    // Fallback when customer matching hasn't run
    customerMatchAttempt = {
      matched: false,
      confidence: 0,
      customer_code: null,
      reasoning: 'Customer matching not executed'
    };
  }

  // Determine failure type based on available data and execution context
  let failureType = 'unknown';
  let failureReason = 'Unknown system error';
  let suggestedActions = [];
  let urgencyLevel = classification.urgency || 'normal';

  // Analyze failure based on current context
  if (!classification.is_invoice_request) {
    failureType = 'non_invoice_request';
    failureReason = 'Request was not classified as an invoice request';
    suggestedActions = [
      'Manual review of email content required',
      'Verify customer intent',
      'Route to appropriate department'
    ];
  } else if (!customerMatchAttempt?.matched) {
    failureType = 'customer_not_found';
    failureReason = 'Customer could not be identified in our system';
    suggestedActions = [
      'Verify customer email address',
      'Check if customer is registered',
      'Manual customer lookup required'
    ];
  } else if (classification.order_number && classification.request_type === 'specific_order') {
    failureType = 'order_not_found';
    failureReason = `Requested order ${classification.order_number} could not be located`;
    suggestedActions = [
      'Verify order number',
      'Check order status in SAP',
      'Search alternative order references'
    ];
  } else {
    failureType = 'no_orders_available';
    failureReason = 'No orders found for this customer';
    suggestedActions = [
      'Check customer order history',
      'Verify customer account status',
      'Contact customer for clarification'
    ];
  }

  // Create comprehensive ticket data
  const timestamp = new Date().toISOString().replace(/[-:]/g, '').slice(0, 15);
  const ticketId = `SAV-${failureType.toUpperCase()}-${timestamp}`;

  // Extract customer info safely
  let customerEmail = 'unknown@example.com';
  let customerName = 'Unknown Customer';
  if (emailData.from?.value?.[0]) {
    customerEmail = emailData.from.value[0].address || customerEmail;
    customerName = emailData.from.value[0].name || customerName;
  }

  const ticketData = {
    status: 'failure_analysis_complete',
    ticket_id: ticketId,
    failure_type: failureType,
    failure_reason: failureReason,
    suggested_actions: suggestedActions,
    title: `AUTOMATED FAILURE - ${failureType.replace(/_/g, ' ').toUpperCase()}`,
    description: `Automated workflow failed to process customer request.\n\nFailure Details:\n- Type: ${failureType}\n- Reason: ${failureReason}\n- Customer: ${customerName} (${customerEmail})\n- Original Request: ${emailData.subject || 'Unknown'}\n- Classification Confidence: ${classification.confidence || 0}%\n- Urgency: ${urgencyLevel}\n\nSuggested Actions:\n${suggestedActions.map(action => `- ${action}`).join('\n')}\n\nOriginal Email Content:\n${emailData.text || 'Content unavailable'}`,
    customer_email: customerEmail,
    customer_name: customerName,
    original_subject: emailData.subject || 'Unknown Request',
    priority: urgencyLevel === 'urgent' ? 'high' : urgencyLevel === 'high' ? 'medium' : 'low',
    category: 'automated_failure',
    classification: classification,
    customer_match_attempt: customerMatchAttempt,
    created_at: new Date().toISOString(),
    requires_human_intervention: true,
    estimated_resolution: urgencyLevel === 'urgent' ? '2 hours' : urgencyLevel === 'high' ? '4 hours' : '24 hours',
    escalation_required: urgencyLevel === 'urgent',
    execution_context: {
      email_data_available: !!emailData,
      classification_available: !!classification,
      customer_match_available: !!customerMatchAttempt
    }
  };

  return [{ json: ticketData }];

} catch (error) {
  // Ultimate fallback for any unexpected errors
  const timestamp = new Date().toISOString().replace(/[-:]/g, '').slice(0, 15);
  const fallbackTicket = {
    status: 'critical_failure',
    ticket_id: `SAV-CRITICAL-${timestamp}`,
    failure_type: 'system_error',
    failure_reason: `Critical system error: ${error.message}`,
    suggested_actions: [
      'Immediate technical investigation required',
      'Check workflow configuration',
      'Review error logs'
    ],
    title: 'CRITICAL - WORKFLOW SYSTEM ERROR',
    description: `Critical failure in automated workflow.\n\nError Details:\n- Message: ${error.message}\n- Timestamp: ${new Date().toISOString()}\n\nImmediate attention required.`,
    customer_email: 'unknown@example.com',
    customer_name: 'Unknown Customer',
    original_subject: 'System Error',
    priority: 'high',
    category: 'system_error',
    classification: null,
    customer_match_attempt: null,
    created_at: new Date().toISOString(),
    requires_human_intervention: true,
    estimated_resolution: '1 hour',
    escalation_required: true
  };

  return [{ json: fallbackTicket }];
}

